import com.example.usermanager.entity.User;
import org.apache.ibatis.annotations.Mapper;
import java.util.List;

@Mapper // ✅ 告诉 Spring：这是一个 MyBatis 接口，请帮我生成代理对象
public interface UserMapper {

    // 查询所有用户
